class Song  attr_accessor :name, :artist, :genre  @@count = 0  @@artists = []  @@genres = []  def initialize(name, artist, genre)    @@count += 1    @@artists << artist    @@genres << genre    @name = name    @artist = artist    @genre = genre  end  def self.count    @@count  end  def self.artists    @@artists = @@artists.uniq  end  def self.genres    @@genres = @@genres.uniq  end  def self.genre_count    histo = {}    @@genres.each do |element|     if histo[element]        histo[element] += 1      else        histo[element] = 1      end     end    histoend